const getGenres = (props) => {
  let {genreIDs, genres} = props
  let movieGenre = []
  genres.map((genre) => {
    if (genreIDs.includes(genre.id)) movieGenre.push(genre.name)
  })
  return movieGenre.join(', ')
}

export default getGenres;
