import Header from "../modules/Header"
import { useState, useEffect, useContext } from "react"
import { SuggestContext } from "../App"

const imagepath = process.env.REACT_APP_POSTER_PATH

const Random = () => {
  const suggested = useContext(SuggestContext)

  const getGenres = (genreIDs) => {
    let movieGenre = []
    suggested.suggestedMovie.genres.map((genre) => {
      if (genreIDs.includes(genre.id)) movieGenre.push(genre.name)
    })
    return movieGenre.join(', ')
  }

  const displayMovie = () => {
    return (
      <div>
        <img className='image' src={imagepath + suggested.suggestedMovie.poster_path}/>
        <div className='movieContent'>
          <div>{suggested.suggestedMovie.title}</div> 
          <div>{getGenres(suggested.suggestedMovie.genre_ids)}</div>
          <div>Release Date: {suggested.suggestedMovie.release_date}</div>
          <div>{suggested.suggestedMovie.overview}</div>
        </div>
      </div>
    )
  }
  return (
    <div>
      <Header/>
      {suggested.loading ? 
        <div>Loading</div> :
        <div>{displayMovie}</div>}
    </div>
  )
}

export default Random;
