import React, {useState, useContext} from 'react';
import { UserContext } from '../App'

const GenreOptions = () => {
  const [isMovieGenre, setIsMovieGenre] = useState(true)

  const user = useContext(UserContext);

  const generateGenres = user.genres.map((genre) => {
    const key = `${genre.name}-${genre.id}`
    return <div onClick={(event) => user.addSelectedGenre(event.target.textContent)} key={key}>{genre.name}</div>
  })

  const generateMovieGenres = user.movieGenre.map((genre) => {
    const key = `${genre.name}-${genre.id}`
    return <div onClick={(event) => user.addSelectedGenre(event.target.textContent)} key={key}>{genre.name}</div>
  })

  const generateTVGenres = user.tvGenre.map((genre) => {
    const key = `${genre.name}-${genre.id}`
    return <div onClick={(event) => user.addSelectedGenre(event.target.textContent)} key={key}>{genre.name}</div>
  })

  const setMovieGenre = () => {
    console.log(user)
    if (!isMovieGenre) {user.resetSelectedGenres([])}
    setIsMovieGenre(true)
  }

  const setTVGenre = () => {
    console.log(user)
    if (isMovieGenre) {user.resetSelectedGenres([])}
    setIsMovieGenre(false)
  }

  return (
    <div className='App-genres'>
      <div className='App-options'>
        <div onClick={setMovieGenre}>
          Movie Genres
        </div>
        <div onClick={setTVGenre}>
          TV Series Genres
        </div>
      </div>

      <div>Choose a genre that you would like to base the generator</div>
      { isMovieGenre ? 
        <div className='Genres'>{generateMovieGenres}</div> : 
        <div className='Genres'>{generateTVGenres}</div>
      }
    </div>
  )
}

export default GenreOptions;
