const makeLink = (type, includedGenres) => {
  let linkGenres = includedGenres.join('%2C%20')
  let lookLink = `https://api.themoviedb.org/3/discover/movie?api_key=${key}&language=en-US&sort_by=popularity.desc`
}

const Generator = (props) => {
  const user = useContext(UserContext);

  return (
    <div className='App-generator'>
      <div>
        Surprise yourself!
