import logo from './logo.svg';
import './App.css';
import React, { useState, useContext } from 'react';
import { 
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from 'react-router-dom';
import Home from './pages/Home';
import Movies from './pages/Movies';
import TVSeries from './pages/TVSeries';

export const UserContext = React.createContext();
const UserProvider = (props) => {
  const {children, generateMovieTV, addSelectedGenre, genres} = props
  return (
    <UserContext.Provider value={{generateMovieTV, addSelectedGenre, genres}}>
      {children}
    </UserContext.Provider>
  )
}

const genres = ['Action', 'Adventure', 'Animation', 'Comedy', 'Crime', 'Documentary', 'Drama', 'Family', 'Fantasy', 'History', 'Horror', 'Music', 'Mystery']

function App() {
  const [selectedGenres, setSelectedGenres] = useState([])

  const addSelectedGenres = (genre) => {
    if (selectedGenres.includes(genre)) return
    let newSelectedGenres = [...selectedGenres]
    newSelectedGenres.push(genre)
    console.log(newSelectedGenres)
    setSelectedGenres(newSelectedGenres)
  }

  const generateMovieTV = () => {
    console.log('Add code for choosing random movie')
  }

  return (
    <Router>
      <UserProvider generateMovieTV={generateMovieTV} addSelectedGenre={addSelectedGenres} genres={genres}>
        <div className="App">
          {/* <Header/>

          <Generator generateMovieTV={generateMovieTV} />

          <GenreOptions addSelectedGenre={addSelectedGenres} genres={genres} /> */}

          <Switch>
            <Route path="/" component={Home}/>
            <Route path="/movies">
              <Movies />
            </Route>
            <Route path="/tv-series">
              <TVSeries />
            </Route>
          </Switch>

        </div>
      </UserProvider>
    </Router>
  );
}

export default App;
