import { FaAngleRight, FaAngleLeft } from "react-icons/fa";
import { useContext } from "react";
import { MoviePaginationContext } from "../pages/Movies";

const Pagination = () => {
  const moviePagination = useContext(MoviePaginationContext)
  const {currentPage, setCurrentPage, lastPage} = moviePagination

  const setNewPage = (newPageNumber) => {
    console.log(newPageNumber)
    setTimeout(300)
    setCurrentPage(newPageNumber)
  }

  const previousPage = () => {
    if (currentPage < 0) return
    setCurrentPage(currentPage-1)
  }

  const nextPage = () => {
    if (currentPage === lastPage) return
    setCurrentPage(currentPage + 1)
  }

  return (
    <div className='pagination'>
      <FaAngleLeft></FaAngleLeft>
      <input className='page-number' type='number' value={currentPage} onChange={(event) => setNewPage(event.target.value)}></input>
      <div>of</div>
      <div>{lastPage}</div>
      <FaAngleRight></FaAngleRight>
    </div>
  )
}

export default Pagination;
