import Header from "../modules/Header";
import React, {useState, useContext, useEffect} from 'react';
import { MovieContext, UserContext } from "../App";
import Pagination from "../modules/Pagination";

export const MoviePaginationContext = React.createContext();

const imagepath = process.env.REACT_APP_POSTER_PATH

const MoviePaginationProvider = (props) => {
  const {children, currentPage, setCurrentPage, lastPage} = props
  return (
    <MoviePaginationContext.Provider value={{currentPage, setCurrentPage, lastPage}}>
      {children}
    </MoviePaginationContext.Provider>
  )
}

const Movies = () => {
  const [currentPage, setCurrentPage] = useState(0) // this will be used for pagination later
  const movies = useContext(MovieContext);
  const user = useContext(UserContext);
  const {movieGenre: genres} = user;
  const {movieList} = movies

  const getGenres = (genreIDs) => {
    let movieGenre = []
    genres.map((genre) => {
      if (genreIDs.includes(genre.id)) movieGenre.push(genre.name)
    })
    return movieGenre.join(', ')
  }

  const generateMovies = movieList.results.map((movie) => {
    return (
      <div key={movie.id} className='movie'>
        <img className='image' alt={movie.title} src={imagepath + movie.poster_path}/>
        <div className='movieContent'>
          <div>{movie.title}</div> 
          <div>{getGenres(movie.genre_ids)}</div>
          <div>Release Date: {movie.release_date}</div>
          <div>{movie.overview}</div>
        </div>
      </div>
    )
  })

  useEffect(() => {
    console.log(movieList)
    setCurrentPage(movieList.page)
  }, [])

  return (
    <div>
      <Header/>
      <div>
        {generateMovies}
      </div>
      <MoviePaginationProvider currentPage={currentPage} setCurrentPage={setCurrentPage} lastPage={movieList.total_pages}>
        <Pagination/>
      </MoviePaginationProvider>
    </div>
  )
}

export default Movies;
